mapper.insert:
    
        SELECT seq_vc.nextval FROM dual
    
	insert into VIDEO_CALL(
		VC_NO
		,USER_NO
		,VC_ID
		,VC_SESSION
		,VC_NAME
		,CREATE_TIMESTAMP
		,MAX_PARTICIPANTS
		,STATUS
		)
	values(
		#{vcNo}
		,#{userNo}
		,#{vcId}
		,#{vcSession}
		,#{vcName}
		,#{createTimestamp}
		,#{maxParticipants}
		,'Y'
		)

mapper.select:
select count(*)
from VIDEO_CALL
where USER_NO = #{userNo}

mapper.update:
update VC_MEMBER
set STATUS = #{status}
	,ROLE_TYPE = #{roleType}
where VC_NO = #{vcNo}
and USER_NO = #{userNo}

mapper.select:
select ROLE_TYPE
from VC_MEMBER
left join on (VC_MEMBER.VC_no = VIDEO_CALL.VC_NO)
where VC_MEMBER.USER_NO = #{userNo}
and VC_MEMBER.VC_ID = #{vcId}

mapper.update:
update VC_MEMBER
set VC_SESSION = #{vcSession}
	,VC_NAME = #{vcName}
	,MAX_PARTICIPANTS = #{maxParticipants}
	,STATUS = #{status}
where VC_NO = #{vcNo}

update VC_MEMBER
set STATUS = "Y"
where USER_NO = #{userNo}
and VC_ID = #{vcId}
